import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Link, useNavigate, useSearchParams } from 'react-router-dom';
import axios from 'axios';
import { 
  ShoppingCart, 
  Heart, 
  User, 
  Search, 
  Menu, 
  X, 
  Mail, 
  Phone, 
  MapPin, 
  LogOut, 
  ArrowRight,
  ShieldCheck,
  Star
} from 'lucide-react';

// --- API CONFIGURATION ---
// This tells React to talk to your Spring Boot backend at http://localhost:8080
const api = axios.create({
  baseURL: '/api',
});

// This automatically attaches your Login Token (JWT) to every request
api.interceptors.request.use((config) => {
  const token = localStorage.getItem('token');
  if (token) {
    config.headers.Authorization = `Bearer ${token}`;
  }
  return config;
});

// --- SUB-COMPONENTS (REPLICATING YOUR HTML FRAGMENTS) ---

const Navbar = ({ user, setUser, cartCount }) => {
  const navigate = useNavigate();

  const handleLogout = () => {
    localStorage.removeItem('token');
    setUser(null);
    navigate('/login');
  };

  return (
    <nav className="navbar navbar-expand-lg navbar-light bg-white shadow-sm sticky-top">
      <div className="container">
        <Link className="navbar-brand" to="/">
          <img src="/images/Chaknik_Logo.png" alt="Anvi Studio" height="50" />
        </Link>
        
        <button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarNav">
          <Menu size={24} />
        </button>

        <div className="collapse navbar-collapse" id="navbarNav">
          <ul className="navbar-nav me-auto mb-2 mb-lg-0 fw-medium">
            <li className="nav-item">
              <Link className="nav-link px-3" to="/">Home</Link>
            </li>
            <li className="nav-item dropdown">
              <a className="nav-link dropdown-toggle px-3" href="#" id="shopDropdown" role="button" data-bs-toggle="dropdown">
                Shop
              </a>
              <ul className="dropdown-menu border-0 shadow mt-2">
                <li><Link className="dropdown-item" to="/products?category=Sarees">Sarees</Link></li>
                <li><Link className="dropdown-item" to="/products?category=Lehengas">Lehengas</Link></li>
                <li><Link className="dropdown-item" to="/products?category=Handlooms">Handlooms</Link></li>
                <li><Link className="dropdown-item" to="/products?category=Kurtis">Kurtis</Link></li>
              </ul>
            </li>
            <li className="nav-item">
              <Link className="nav-link px-3" to="/about">About Us</Link>
            </li>
          </ul>

          <form className="d-flex mx-lg-4 my-2 my-lg-0 w-100" style={{maxWidth: '350px'}}>
            <div className="input-group">
              <input className="form-control border-end-0 bg-light" type="search" placeholder="Search sarees..." />
              <button className="btn btn-light border border-start-0" type="submit">
                <Search size={18} className="text-secondary" />
              </button>
            </div>
          </form>

          <div className="d-flex align-items-center gap-4">
            <Link to="/wishlist" className="text-dark position-relative">
              <Heart size={22} />
            </Link>
            <Link to="/cart" className="text-dark position-relative">
              <ShoppingCart size={22} />
              {cartCount > 0 && (
                <span className="position-absolute top-0 start-100 translate-middle badge rounded-pill bg-danger" style={{fontSize: '10px'}}>
                  {cartCount}
                </span>
              )}
            </Link>
            <div className="dropdown">
              <button className="btn btn-link text-dark p-0 dropdown-toggle shadow-none" data-bs-toggle="dropdown">
                <User size={22} />
              </button>
              <ul className="dropdown-menu dropdown-menu-end shadow border-0 mt-3 p-2" style={{minWidth: '200px'}}>
                {user ? (
                  <>
                    <li className="px-3 py-2 border-bottom mb-2 text-center">
                      <div className="small text-muted">Welcome,</div>
                      <div className="fw-bold text-truncate">{user.username}</div>
                    </li>
                    <li><Link className="dropdown-item rounded" to={user.role === 'ADMIN' ? '/admin' : '/dashboard'}>My Profile</Link></li>
                    <li><Link className="dropdown-item rounded" to="/orders">Order History</Link></li>
                    <li><hr className="dropdown-divider" /></li>
                    <li>
                      <button className="dropdown-item text-danger rounded d-flex align-items-center gap-2" onClick={handleLogout}>
                        <LogOut size={16} /> Logout
                      </button>
                    </li>
                  </>
                ) : (
                  <>
                    <li><Link className="dropdown-item rounded" to="/login">Login</Link></li>
                    <li><Link className="dropdown-item rounded" to="/register">Create Account</Link></li>
                  </>
                )}
              </ul>
            </div>
          </div>
        </div>
      </div>
    </nav>
  );
};

const Footer = () => {
  return (
    <footer className="bg-dark text-light pt-5 pb-3 mt-5">
      <div className="container">
        <div className="row g-4">
          <div className="col-md-4">
            <img src="/images/Chaknik_Logo.png" alt="Logo" height="45" className="mb-4" style={{filter: 'brightness(0) invert(1)'}} />
            <p className="text-secondary small pe-lg-5">Anvi Studio brings you the finest handpicked traditional and contemporary wear from the heart of India. Quality and Elegance in every thread.</p>
            <div className="d-flex gap-3 small align-items-center">
              <Phone size={16} className="text-primary" /> <span>+91 98765 43210</span>
            </div>
            <div className="d-flex gap-3 mt-2 small align-items-center">
              <Mail size={16} className="text-primary" /> <span>info@anvistudio.com</span>
            </div>
          </div>
          <div className="col-md-2">
            <h6 className="fw-bold mb-4 text-uppercase">Shop</h6>
            <ul className="list-unstyled text-secondary small">
              <li className="mb-2"><Link to="/products?category=Sarees" className="text-decoration-none text-secondary">Sarees</Link></li>
              <li className="mb-2"><Link to="/products?category=Lehengas" className="text-decoration-none text-secondary">Lehengas</Link></li>
              <li className="mb-2"><Link to="/products?category=Kurtis" className="text-decoration-none text-secondary">Kurtis</Link></li>
              <li className="mb-2"><Link to="/products" className="text-decoration-none text-secondary">New Arrivals</Link></li>
            </ul>
          </div>
          <div className="col-md-3">
            <h6 className="fw-bold mb-4 text-uppercase">Support</h6>
            <ul className="list-unstyled text-secondary small">
              <li className="mb-2"><Link to="/contact" className="text-decoration-none text-secondary">Contact Us</Link></li>
              <li className="mb-2"><Link to="/shipping" className="text-decoration-none text-secondary">Shipping Info</Link></li>
              <li className="mb-2"><Link to="/returns" className="text-decoration-none text-secondary">Returns & Exchange</Link></li>
              <li className="mb-2"><Link to="/privacy" className="text-decoration-none text-secondary">Privacy Policy</Link></li>
            </ul>
          </div>
          <div className="col-md-3">
            <h6 className="fw-bold mb-4 text-uppercase">Stay Updated</h6>
            <p className="small text-secondary">Subscribe for early access to sales and new drops.</p>
            <div className="input-group">
              <input type="email" className="form-control bg-dark text-white border-secondary small" placeholder="Email Address" />
              <button className="btn btn-outline-light btn-sm">Join</button>
            </div>
          </div>
        </div>
        <hr className="mt-5 border-secondary opacity-25" />
        <div className="d-flex flex-column flex-md-row justify-content-between align-items-center gap-3 mt-4">
          <p className="text-secondary small mb-0"> 2024 Anvi Studio. Designed for Elegance.</p>
          <div className="d-flex gap-3">
            <img src="/images/payment-icons.png" alt="Payments" height="20" style={{opacity: 0.5}} />
          </div>
        </div>
      </div>
    </footer>
  );
};

// --- CORE PAGES ---

const HomePage = () => {
  const [featured, setFeatured] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    api.get('/products/featured')
      .then(res => setFeatured(res.data))
      .catch(err => console.error("Could not load products", err))
      .finally(() => setLoading(false));
  }, []);

  const categories = [
    { name: 'Sarees', img: '/images/shopcategory/sarees.png' },
    { name: 'Lehengas', img: '/images/shopcategory/lehengas.png' },
    { name: 'Kurthis', img: '/images/shopcategory/kurthi&suits.png' },
    { name: 'Kidswear', img: '/images/shopcategory/kidswear.png' }
  ];

  return (
    <div className="animate-fade-in">
      {/* Hero Section */}
      <section className="position-relative text-white mb-5" style={{height: '550px'}}>
        <div className="position-absolute w-100 h-100" style={{
          backgroundImage: 'linear-gradient(rgba(0,0,0,0.5), rgba(0,0,0,0.3)), url(/images/licensed-image.png)',
          backgroundSize: 'cover',
          backgroundPosition: 'center'
        }}></div>
        <div className="container h-100 position-relative d-flex align-items-center">
          <div className="col-lg-6">
            <h1 className="display-2 fw-bold mb-3">Redefining <br/>Indian Elegance</h1>
            <p className="lead mb-4 opacity-75 pe-md-5">Handcrafted sarees and lehengas designed to celebrate your most precious moments.</p>
            <div className="d-flex gap-3">
              <Link to="/products" className="btn btn-light btn-lg px-5 fw-bold rounded-0">Shop Now</Link>
              <Link to="/about" className="btn btn-outline-light btn-lg px-4 rounded-0">Our Story</Link>
            </div>
          </div>
        </div>
      </section>

      <div className="container">
        {/* Categories */}
        <div className="text-center mb-5">
          <h2 className="fw-bold">Discover Collections</h2>
          <div className="mx-auto bg-dark" style={{height: '2px', width: '60px'}}></div>
        </div>
        
        <div className="row g-4 mb-5 pb-5">
          {categories.map((cat, idx) => (
            <div key={idx} className="col-6 col-md-3">
              <Link to={`/products?category=${cat.name}`} className="text-decoration-none text-dark group">
                <div className="card h-100 border-0 shadow-sm overflow-hidden">
                  <div className="overflow-hidden position-relative" style={{height: '300px'}}>
                    <img src={cat.img} className="card-img-top h-100 w-100 object-fit-cover transition-transform" alt={cat.name} />
                    <div className="position-absolute w-100 h-100 top-0 start-0 bg-dark bg-opacity-10 transition-opacity"></div>
                  </div>
                  <div className="card-body text-center bg-white py-3">
                    <h6 className="fw-bold mb-0 text-uppercase tracking-wider small">{cat.name}</h6>
                  </div>
                </div>
              </Link>
            </div>
          ))}
        </div>

        {/* Featured Products */}
        <div className="d-flex justify-content-between align-items-end mb-4 pt-5">
          <div>
            <h2 className="fw-bold mb-1">New Arrivals</h2>
            <p className="text-muted small">The latest additions to our designer vault</p>
          </div>
          <Link to="/products" className="text-dark fw-bold small text-decoration-none d-flex align-items-center gap-1 mb-2">
            View All <ArrowRight size={16} />
          </Link>
        </div>

        <div className="row g-4 mb-5">
          {loading ? (
            <div className="col-12 text-center py-5 text-muted">
              <div className="spinner-border spinner-border-sm me-2 text-dark"></div> Loading the collection...
            </div>
          ) : featured.length > 0 ? (
            featured.map((p) => (
              <div key={p.id} className="col-6 col-md-3">
                <div className="card h-100 border-0 shadow-sm transition-hover">
                  <div className="position-relative overflow-hidden" style={{height: '350px'}}>
                    <img src={p.imageUrl || '/images/sarees/saree-image.jpg'} className="card-img-top h-100 w-100 object-fit-cover" alt={p.name} />
                    {p.discountPercent > 0 && (
                      <span className="position-absolute top-0 start-0 bg-danger text-white px-2 py-1 small m-3 fw-bold">-{p.discountPercent}%</span>
                    )}
                  </div>
                  <div className="card-body p-3">
                    <h6 className="text-truncate fw-bold mb-1">{p.name}</h6>
                    <div className="d-flex align-items-center gap-2 mb-3">
                      <span className="fw-bold fs-5">{p.discountedPrice}</span>
                      {p.price > p.discountedPrice && (
                        <span className="text-muted text-decoration-line-through small">{p.price}</span>
                      )}
                    </div>
                    <Link to={`/product/${p.id}`} className="btn btn-dark btn-sm w-100 py-2 rounded-0 fw-bold">Quick View</Link>
                  </div>
                </div>
              </div>
            ))
          ) : (
            <div className="col-12 text-center py-5 border rounded bg-light">
              <p className="text-muted mb-0">Our products are currently being updated. Please check back soon!</p>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

const LoginPage = ({ onLoginSuccess }) => {
  const [creds, setCreds] = useState({ username: '', password: '' });
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();
    setError('');
    setLoading(true);
    try {
      const res = await api.post('/auth/login', creds);
      localStorage.setItem('token', res.data.token);
      onLoginSuccess(res.data.user);
      navigate(res.data.user.role === 'ADMIN' ? '/admin' : '/');
    } catch (err) {
      setError(err.response?.data?.error || 'Wrong credentials. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="container py-5 mt-4">
      <div className="row justify-content-center">
        <div className="col-md-5">
          <div className="card border-0 shadow-lg p-4 p-md-5">
            <h2 className="text-center fw-bold mb-4">Welcome Back</h2>
            {error && <div className="alert alert-danger py-2 small mb-4">{error}</div>}
            <form onSubmit={handleLogin}>
              <div className="mb-3">
                <label className="form-label small fw-bold">Email or Username</label>
                <input 
                  type="text" 
                  className="form-control py-2 shadow-none" 
                  onChange={(e) => setCreds({...creds, username: e.target.value})} 
                  required 
                />
              </div>
              <div className="mb-4">
                <div className="d-flex justify-content-between mb-1">
                  <label className="form-label small fw-bold">Password</label>
                  <Link to="/forgot-password" size="sm" className="text-decoration-none small">Forgot?</Link>
                </div>
                <input 
                  type="password" 
                  className="form-control py-2 shadow-none" 
                  onChange={(e) => setCreds({...creds, password: e.target.value})} 
                  required 
                />
              </div>
              <button className="btn btn-dark w-100 py-2 fw-bold mb-4" disabled={loading}>
                {loading ? 'Authenticating...' : 'Sign In'}
              </button>
              <p className="text-center small text-muted mb-0">
                New to Anvi Studio? <Link to="/register" className="fw-bold text-dark">Create an account</Link>
              </p>
            </form>
          </div>
        </div>
      </div>
    </div>
  );
};

const RegisterPage = () => {
  const [form, setForm] = useState({ 
    username: '', firstName: '', lastName: '', mobileNumber: '', password: '', confirmPassword: '' 
  });
  const [msg, setMsg] = useState({ text: '', type: '' });
  const navigate = useNavigate();

  const handleRegister = async (e) => {
    e.preventDefault();
    if(form.password !== form.confirmPassword) return setMsg({text: 'Passwords do not match', type: 'danger'});
    
    try {
      const res = await api.post('/auth/register', form);
      setMsg({ text: res.data.message, type: 'success' });
      setTimeout(() => navigate(`/verify-otp?email=${form.username}`), 2500);
    } catch (err) {
      setMsg({ text: err.response?.data?.error || 'Registration failed', type: 'danger' });
    }
  };

  return (
    <div className="container py-5 mt-4">
      <div className="row justify-content-center">
        <div className="col-md-7">
          <div className="card border-0 shadow-lg p-4 p-md-5">
            <h2 className="text-center fw-bold mb-4">Create Account</h2>
            {msg.text && <div className={`alert alert-${msg.type} py-2 small mb-4`}>{msg.text}</div>}
            <form onSubmit={handleRegister}>
              <div className="row g-3">
                <div className="col-md-6">
                  <label className="form-label small fw-bold">First Name</label>
                  <input type="text" className="form-control shadow-none" onChange={(e)=>setForm({...form, firstName: e.target.value})} required />
                </div>
                <div className="col-md-6">
                  <label className="form-label small fw-bold">Last Name</label>
                  <input type="text" className="form-control shadow-none" onChange={(e)=>setForm({...form, lastName: e.target.value})} required />
                </div>
                <div className="col-md-12">
                  <label className="form-label small fw-bold">Email Address</label>
                  <input type="email" className="form-control shadow-none" onChange={(e)=>setForm({...form, username: e.target.value})} required />
                </div>
                <div className="col-md-12">
                  <label className="form-label small fw-bold">Mobile Number</label>
                  <input type="text" className="form-control shadow-none" onChange={(e)=>setForm({...form, mobileNumber: e.target.value})} required />
                </div>
                <div className="col-md-6">
                  <label className="form-label small fw-bold">Password</label>
                  <input type="password" className="form-control shadow-none" onChange={(e)=>setForm({...form, password: e.target.value})} required />
                </div>
                <div className="col-md-6">
                  <label className="form-label small fw-bold">Confirm Password</label>
                  <input type="password" className="form-control shadow-none" onChange={(e)=>setForm({...form, confirmPassword: e.target.value})} required />
                </div>
              </div>
              <button className="btn btn-dark w-100 py-2 mt-4 fw-bold">Register Now</button>
              <p className="text-center small text-muted mt-4 mb-0">
                Already have an account? <Link to="/login" className="fw-bold text-dark">Sign In</Link>
              </p>
            </form>
          </div>
        </div>
      </div>
    </div>
  );
};

const VerifyOtpPage = () => {
  const [params] = useSearchParams();
  const email = params.get('email');
  const [otp, setOtp] = useState('');
  const [msg, setMsg] = useState({ text: '', type: '' });
  const navigate = useNavigate();

  const handleVerify = async (e) => {
    e.preventDefault();
    try {
      const res = await api.post(`/auth/verify-otp?otp=${otp}&email=${email}`);
      setMsg({ text: res.data.message, type: 'success' });
      setTimeout(() => navigate('/login'), 2000);
    } catch (err) {
      setMsg({ text: err.response?.data?.error || 'Invalid Code', type: 'danger' });
    }
  };

  return (
    <div className="container py-5 mt-5">
      <div className="row justify-content-center">
        <div className="col-md-5">
          <div className="card border-0 shadow-lg text-center p-5">
            <div className="mb-4">
              <div className="bg-light d-inline-block p-3 rounded-circle text-primary">
                <ShieldCheck size={40} />
              </div>
            </div>
            <h3 className="fw-bold">Security Check</h3>
            <p className="text-muted small px-3">We have sent a 6-digit verification code to your email <b>{email}</b>. Please enter it below.</p>
            {msg.text && <div className={`alert alert-${msg.type} py-2 small mb-3`}>{msg.text}</div>}
            <form onSubmit={handleVerify}>
              <input 
                type="text" 
                className="form-control form-control-lg text-center fw-bold fs-2 mb-4 tracking-widest shadow-none" 
                maxLength="6" 
                placeholder="000000"
                onChange={(e)=>setOtp(e.target.value)}
                required
              />
              <button className="btn btn-dark w-100 py-3 fw-bold">Verify Account</button>
            </form>
          </div>
        </div>
      </div>
    </div>
  );
};

// --- MAIN APP ---

export default function App() {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const initAuth = async () => {
      const token = localStorage.getItem('token');
      if (token) {
        try {
          const res = await api.get('/auth/me');
          setUser(res.data);
        } catch (err) {
          localStorage.removeItem('token');
        }
      }
      setLoading(false);
    };
    initAuth();
  }, []);

  if (loading) {
    return (
      <div className="d-flex justify-content-center align-items-center vh-100 bg-white">
        <div className="text-center">
          <img src="/images/Chaknik_Logo.png" alt="Loading" height="60" className="mb-4 animate-pulse" />
          <div className="spinner-border spinner-border-sm text-dark d-block mx-auto" role="status"></div>
        </div>
      </div>
    );
  }

  return (
    <Router>
      <div className="d-flex flex-column min-vh-100 bg-light-subtle">
        <Navbar user={user} setUser={setUser} cartCount={0} />
        
        <main className="flex-grow-1">
          <Routes>
            <Route path="/" element={<HomePage />} />
            <Route path="/login" element={<LoginPage onLoginSuccess={setUser} />} />
            <Route path="/register" element={<RegisterPage />} />
            <Route path="/verify-otp" element={<VerifyOtpPage />} />
            
            {/* Generic Placeholders for Now */}
            <Route path="/products" element={<div className="container py-5 text-center"><h2>Collection Page coming next</h2></div>} />
            <Route path="/about" element={<div className="container py-5 text-center"><h2>About Anvi Studio</h2></div>} />
            
            <Route path="*" element={
              <div className="container py-5 text-center my-5">
                <h1 className="display-1 fw-bold text-muted">404</h1>
                <p className="lead">Oops! The page you're looking for went out of style.</p>
                <Link to="/" className="btn btn-dark px-4 mt-3">Back Home</Link>
              </div>
            } />
          </Routes>
        </main>

        <Footer />
      </div>

      <style>{`
        .animate-fade-in { animation: fadeIn 0.6s ease-out; }
        @keyframes fadeIn { from { opacity: 0; transform: translateY(10px); } to { opacity: 1; transform: translateY(0); } }
        .transition-transform { transition: transform 0.4s ease; }
        .group:hover .transition-transform { transform: scale(1.08); }
        .transition-hover { transition: all 0.3s ease; }
        .transition-hover:hover { transform: translateY(-5px); box-shadow: 0 1rem 3rem rgba(0,0,0,.1) !important; }
        .tracking-wider { letter-spacing: 1px; }
        .tracking-widest { letter-spacing: 12px; }
        .animate-pulse { animation: pulse 1.5s infinite; }
        @keyframes pulse { 0% { opacity: 1; } 50% { opacity: 0.5; } 100% { opacity: 1; } }
      `}</style>
    </Router>
  );
}
