import React, { createContext, useContext, useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Link, useNavigate, useSearchParams } from 'react-router-dom';
import axios from 'axios';
import { 
  ShoppingCart, 
  Heart, 
  User, 
  Search, 
  Menu, 
  X, 
  Mail, 
  Phone, 
  LogOut, 
  ArrowRight,
  ShieldCheck,
  Star,
  ShoppingBag,
  Facebook,
  Instagram,
  Twitter,
  ChevronDown,
  Filter,
  SlidersHorizontal,
  LayoutGrid,
  List
} from 'lucide-react';

// --- 1. API CLIENT CONFIGURATION ---
const apiClient = axios.create({
  baseURL: 'http://localhost:8080/api',
  headers: {
    'Content-Type': 'application/json',
  },
});

apiClient.interceptors.request.use(
  (config) => {
    const token = localStorage.getItem('token');
    if (token) {
      config.headers.Authorization = `Bearer ${token}`;
    }
    return config;
  },
  (error) => Promise.reject(error)
);

apiClient.interceptors.response.use(
  (response) => response,
  (error) => {
    if (error.response?.status === 401) {
      localStorage.removeItem('token');
      if (window.location.pathname !== '/login') {
        window.location.href = '/login';
      }
    }
    return Promise.reject(error);
  }
);

// --- 2. AUTH CONTEXT ---
const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const checkAuth = async () => {
      const token = localStorage.getItem('token');
      if (token) {
        try {
          const response = await apiClient.get('/auth/me');
          setUser(response.data);
        } catch (error) {
          localStorage.removeItem('token');
        }
      }
      setLoading(false);
    };
    checkAuth();
  }, []);

  const login = async (username, password) => {
    try {
      const response = await apiClient.post('/auth/login', { username, password });
      const { token, user: userData } = response.data;
      localStorage.setItem('token', token);
      setUser(userData);
      return { success: true };
    } catch (error) {
      return { success: false, message: error.response?.data?.error || "Login failed." };
    }
  };

  const logout = () => {
    localStorage.removeItem('token');
    setUser(null);
  };

  return (
    <AuthContext.Provider value={{ user, loading, login, logout, isAuthenticated: !!user, isAdmin: user?.role === 'ADMIN' }}>
      {!loading && children}
    </AuthContext.Provider>
  );
};

export const useAuth = () => useContext(AuthContext);

// --- 3. UI COMPONENTS ---

const AnnouncementBar = () => (
  <div className="bg-black text-white text-center py-2.5 text-[10px] font-bold tracking-[0.2em] uppercase">
    ✨ Free Shipping on orders over ₹2,000 | Discover the New Festive Silk Collection ✨
  </div>
);

const Navbar = () => {
  const { user, logout, isAuthenticated } = useAuth();
  
  return (
    <header className="bg-white border-b sticky top-0 z-50">
      <div className="container mx-auto px-4 py-4 flex items-center justify-between">
        <div className="w-1/3">
          <div className="relative max-w-xs hidden md:block">
            <input 
              type="text" 
              placeholder="Search boutique..." 
              className="w-full bg-gray-50 border border-gray-200 rounded-full py-2 px-4 pl-10 text-xs focus:ring-1 focus:ring-black outline-none"
            />
            <Search className="absolute left-3 top-2.5 text-gray-400" size={14} />
          </div>
        </div>

        <div className="w-1/3 text-center">
          <Link to="/" className="text-2xl font-serif font-black tracking-tighter">ANVI STUDIO</Link>
        </div>

        <div className="w-1/3 flex items-center justify-end space-x-6">
          <div className="group relative">
            <button className="flex items-center space-x-1.5 text-gray-700 hover:text-black">
              <User size={20} />
              <span className="text-[10px] font-bold uppercase hidden lg:inline tracking-wider">
                {isAuthenticated ? user.username : 'Account'}
              </span>
            </button>
            <div className="absolute right-0 top-full hidden group-hover:block bg-white shadow-xl border rounded-sm w-48 py-2 z-50">
              {isAuthenticated ? (
                <>
                  <Link to="/profile" className="block px-4 py-2 text-xs hover:bg-gray-50">My Profile</Link>
                  <Link to="/orders" className="block px-4 py-2 text-xs hover:bg-gray-50">My Orders</Link>
                  <button onClick={logout} className="w-full text-left px-4 py-2 text-xs text-red-600 hover:bg-gray-50">Logout</button>
                </>
              ) : (
                <>
                  <Link to="/login" className="block px-4 py-2 text-xs hover:bg-gray-50">Login</Link>
                  <Link to="/register" className="block px-4 py-2 text-xs hover:bg-gray-50">Register</Link>
                </>
              )}
            </div>
          </div>
          <Link to="/cart" className="text-gray-700 hover:text-black relative">
            <ShoppingBag size={20} />
            <span className="absolute -top-1.5 -right-1.5 bg-black text-white text-[9px] w-4 h-4 rounded-full flex items-center justify-center font-bold">0</span>
          </Link>
        </div>
      </div>
      
      <nav className="border-t py-3">
        <ul className="flex justify-center space-x-10 text-[10px] font-bold uppercase tracking-widest">
          <li><Link to="/" className="hover:text-gray-400">Home</Link></li>
          <li><Link to="/products" className="hover:text-gray-400">Shop All</Link></li>
          <li><Link to="/products?category=Sarees" className="hover:text-gray-400">Sarees</Link></li>
          <li><Link to="/products?category=Lehengas" className="hover:text-gray-400">Lehengas</Link></li>
          <li><Link to="/products?category=Handlooms" className="hover:text-gray-400">Handlooms</Link></li>
        </ul>
      </nav>
    </header>
  );
};

// --- 4. SHOP PAGE COMPONENTS ---

const ProductCatalog = () => {
  const [searchParams, setSearchParams] = useSearchParams();
  const [products, setProducts] = useState([]);
  const [categories, setCategories] = useState([]);
  const [loading, setLoading] = useState(true);
  const [isFilterOpen, setIsFilterOpen] = useState(false);

  // Get current filters from URL
  const currentCategory = searchParams.get('category') || '';
  const currentSort = searchParams.get('sortBy') || 'latest';

  useEffect(() => {
    // Fetch categories for the sidebar
    apiClient.get('/products/categories').then(res => setCategories(res.data));
  }, []);

  useEffect(() => {
    setLoading(true);
    // Fetch filtered products based on URL params
    const params = {
      category: currentCategory || undefined,
      sortBy: currentSort
    };

    apiClient.get('/products', { params })
      .then(res => {
        setProducts(res.data);
        setLoading(false);
      })
      .catch(() => setLoading(false));
  }, [currentCategory, currentSort]);

  const updateFilters = (key, value) => {
    const newParams = new URLSearchParams(searchParams);
    if (value) {
      newParams.set(key, value);
    } else {
      newParams.delete(key);
    }
    setSearchParams(newParams);
  };

  return (
    <div className="bg-white min-h-screen">
      <div className="container mx-auto px-4 py-8">
        {/* Page Header */}
        <div className="flex flex-col md:flex-row md:items-end justify-between mb-8 border-b pb-6">
          <div>
            <nav className="text-[10px] uppercase tracking-widest text-gray-400 mb-2">
              <Link to="/" className="hover:text-black">Home</Link> / <span>Shop</span>
            </nav>
            <h1 className="text-3xl font-serif font-bold tracking-tight">
              {currentCategory || "All Collections"}
            </h1>
          </div>
          
          <div className="flex items-center space-x-4 mt-6 md:mt-0">
            <div className="relative group">
              <select 
                className="appearance-none bg-transparent pr-8 py-2 text-[10px] font-bold uppercase tracking-widest border-none focus:ring-0 cursor-pointer"
                value={currentSort}
                onChange={(e) => updateFilters('sortBy', e.target.value)}
              >
                <option value="latest">Sort: Newest</option>
                <option value="priceLow">Price: Low to High</option>
                <option value="priceHigh">Price: High to Low</option>
                <option value="popular">Popularity</option>
              </select>
              <ChevronDown size={12} className="absolute right-0 top-3 pointer-events-none" />
            </div>
            
            <button 
              onClick={() => setIsFilterOpen(!isFilterOpen)}
              className="md:hidden flex items-center space-x-2 text-[10px] font-bold uppercase tracking-widest"
            >
              <SlidersHorizontal size={14} />
              <span>Filters</span>
            </button>
          </div>
        </div>

        <div className="flex gap-12">
          {/* Sidebar Filters */}
          <aside className={`md:w-64 flex-shrink-0 ${isFilterOpen ? 'block' : 'hidden'} md:block`}>
            <div className="sticky top-32">
              <div className="mb-10">
                <h4 className="text-[10px] font-bold uppercase tracking-[0.2em] mb-6 border-b pb-2">Categories</h4>
                <ul className="space-y-3">
                  <li>
                    <button 
                      onClick={() => updateFilters('category', '')}
                      className={`text-xs uppercase tracking-wide transition-colors ${!currentCategory ? 'font-bold text-black border-l-2 border-black pl-3' : 'text-gray-500 hover:text-black pl-3'}`}
                    >
                      All Items
                    </button>
                  </li>
                  {categories.map((cat, i) => (
                    <li key={i}>
                      <button 
                        onClick={() => updateFilters('category', cat)}
                        className={`text-xs uppercase tracking-wide text-left w-full transition-colors ${currentCategory === cat ? 'font-bold text-black border-l-2 border-black pl-3' : 'text-gray-500 hover:text-black pl-3'}`}
                      >
                        {cat}
                      </button>
                    </li>
                  ))}
                </ul>
              </div>

              <div>
                <h4 className="text-[10px] font-bold uppercase tracking-[0.2em] mb-6 border-b pb-2">Filter By Price</h4>
                <div className="space-y-4">
                  {/* Visual slider mock or specific ranges */}
                  {[
                    { label: 'Under ₹5,000', range: [0, 5000] },
                    { label: '₹5,000 - ₹15,000', range: [5000, 15000] },
                    { label: 'Above ₹15,000', range: [15000, 100000] }
                  ].map((range, i) => (
                    <label key={i} className="flex items-center space-x-3 cursor-pointer group">
                      <div className="w-3 h-3 border border-gray-300 group-hover:border-black transition-colors"></div>
                      <span className="text-[10px] uppercase font-medium tracking-wider text-gray-500 group-hover:text-black">{range.label}</span>
                    </label>
                  ))}
                </div>
              </div>
            </div>
          </aside>

          {/* Product Grid */}
          <div className="flex-grow">
            {loading ? (
              <div className="grid grid-cols-2 lg:grid-cols-3 gap-x-8 gap-y-12">
                {[...Array(6)].map((_, i) => (
                  <div key={i} className="animate-pulse">
                    <div className="bg-gray-100 aspect-[3/4] mb-4"></div>
                    <div className="h-3 bg-gray-100 w-3/4 mb-2"></div>
                    <div className="h-3 bg-gray-100 w-1/4"></div>
                  </div>
                ))}
              </div>
            ) : products.length > 0 ? (
              <div className="grid grid-cols-2 lg:grid-cols-3 xl:grid-cols-3 gap-x-8 gap-y-16">
                {products.map((product) => (
                  <div key={product.id} className="group cursor-pointer">
                    <div className="relative aspect-[3/4] bg-gray-50 overflow-hidden mb-6">
                      <img 
                        src={product.imageUrl || 'https://images.unsplash.com/photo-1610030469983-98e550d6193c?auto=format&fit=crop&q=80&w=800'} 
                        alt={product.name}
                        className="w-full h-full object-cover transition-transform duration-700 group-hover:scale-105"
                      />
                      {/* Hover Overlay */}
                      <div className="absolute inset-0 bg-black/5 opacity-0 group-hover:opacity-100 transition-opacity flex items-end p-4">
                        <button className="w-full bg-white text-black py-3 text-[10px] font-bold uppercase tracking-widest hover:bg-black hover:text-white transition-colors">
                          Add To Cart
                        </button>
                      </div>
                      {product.discountPercent > 0 && (
                        <div className="absolute top-4 left-4 bg-black text-white px-2 py-1 text-[9px] font-bold tracking-widest">
                          -{product.discountPercent}%
                        </div>
                      )}
                      <button className="absolute top-4 right-4 text-gray-400 hover:text-black transition-colors">
                        <Heart size={18} />
                      </button>
                    </div>
                    
                    <div className="text-center">
                      <p className="text-[9px] uppercase tracking-widest text-gray-400 mb-1 font-bold">{product.categoryName}</p>
                      <h3 className="text-xs font-bold uppercase tracking-wider text-gray-800 mb-2 truncate">
                        {product.name}
                      </h3>
                      <div className="flex items-center justify-center space-x-3">
                        <span className="text-sm font-bold tracking-tight">₹{product.discountedPrice.toLocaleString()}</span>
                        {product.price > product.discountedPrice && (
                          <span className="text-[10px] text-gray-400 line-through">₹{product.price.toLocaleString()}</span>
                        )}
                      </div>
                    </div>
                  </div>
                ))}
              </div>
            ) : (
              <div className="py-20 text-center border border-dashed border-gray-200 rounded">
                <p className="text-xs font-bold uppercase tracking-widest text-gray-400">No products found matching your selection.</p>
                <button 
                  onClick={() => updateFilters('category', '')}
                  className="mt-4 text-[10px] font-bold uppercase tracking-widest border-b border-black pb-1"
                >
                  View All Products
                </button>
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

