      <p className="text-gray-500 text-xs mb-8">Crafting elegance through tradition since 2024.</p>
      <div className="flex justify-center space-x-6 text-gray-400 mb-8">
        <Facebook size={18} /> <Instagram size={18} /> <Twitter size={18} />
      </div>
      <p className="text-[10px] text-gray-400 uppercase tracking-widest font-bold">&copy; Anvi Studio Boutique. All Rights Reserved.</p>
    </div>
  </footer>
);

// --- 6. MAIN APP ---

export default function App() {
  return (
    <AuthProvider>
      <Router>
        <div className="flex flex-col min-h-screen bg-white">
          <AnnouncementBar />
          <Navbar />
          <main className="flex-grow">
            <Routes>
              <Route path="/" element={<div className="p-20 text-center font-serif text-3xl">Home Page Placeholder</div>} />
              <Route path="/products" element={<ProductCatalog />} />
              <Route path="/login" element={<div className="p-20 text-center uppercase tracking-widest font-bold">Login Placeholder</div>} />
              <Route path="/register" element={<div className="p-20 text-center uppercase tracking-widest font-bold">Register Placeholder</div>} />
            </Routes>
          </main>
          <Footer />
        </div>
      </Router>
    </AuthProvider>
  );
}
