import React, { createContext, useContext, useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Link, useNavigate, useSearchParams } from 'react-router-dom';
import axios from 'axios';
import { 
  ShoppingCart, 
  Heart, 
  User, 
  Search, 
  Menu, 
  X, 
  LogOut, 
  ShoppingBag,
  ChevronDown,
  Phone,
  Mail,
  Facebook,
  Instagram,
  Twitter
} from 'lucide-react';

// --- API CLIENT CONFIGURATION ---
const apiClient = axios.create({
  baseURL: 'http://localhost:8080/api',
  headers: {
    'Content-Type': 'application/json',
  },
});

apiClient.interceptors.request.use(
  (config) => {
    const token = localStorage.getItem('token');
    if (token) {
      config.headers.Authorization = `Bearer ${token}`;
    }
    return config;
  },
  (error) => Promise.reject(error)
);

apiClient.interceptors.response.use(
  (response) => response,
  (error) => {
    if (error.response?.status === 401) {
      localStorage.removeItem('token');
      if (window.location.pathname !== '/login') {
        window.location.href = '/login';
      }
    }
    return Promise.reject(error);
  }
);

// --- AUTH CONTEXT ---
const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const checkAuth = async () => {
      const token = localStorage.getItem('token');
      if (token) {
        try {
          const response = await apiClient.get('/auth/me');
          setUser(response.data);
        } catch (error) {
          localStorage.removeItem('token');
        }
      }
      setLoading(false);
    };
    checkAuth();
  }, []);

  const login = async (username, password) => {
    try {
      const response = await apiClient.post('/auth/login', { username, password });
      const { token, user: userData } = response.data;
      localStorage.setItem('token', token);
      setUser(userData);
      return { success: true };
    } catch (error) {
      return { success: false, message: error.response?.data?.error || "Login failed." };
    }
  };

  const logout = () => {
    localStorage.removeItem('token');
    setUser(null);
  };

  const register = async (data) => {
    try {
      const response = await apiClient.post('/auth/register', data);
      return { success: true, message: response.data.message };
    } catch (error) {
      return { success: false, message: error.response?.data?.error || "Registration failed." };
    }
  };

  return (
    <AuthContext.Provider value={{ user, loading, login, logout, register, isAuthenticated: !!user, isAdmin: user?.role === 'ADMIN' }}>
      {!loading && children}
    </AuthContext.Provider>
  );
};

export const useAuth = () => useContext(AuthContext);

// --- UI COMPONENTS ---

const AnnouncementBar = () => (
  <div className="bg-black text-white text-center py-2 text-[10px] font-bold tracking-widest uppercase">
    ✨ Free Shipping on orders over ₹2,000 | Shop the Festive Silk Collection ✨
  </div>
);

const Navbar = () => {
  const { user, logout, isAuthenticated } = useAuth();
  const [isMenuOpen, setIsMenuOpen] = useState(false);

  return (
    <header className="bg-white border-b sticky top-0 z-50">
      <div className="container mx-auto px-4 py-4">
        <div className="flex items-center justify-between">
          {/* Mobile Toggle */}
          <button className="lg:hidden text-gray-700" onClick={() => setIsMenuOpen(!isMenuOpen)}>
            {isMenuOpen ? <X size={24} /> : <Menu size={24} />}
          </button>

          {/* Left: Search (Desktop) */}
          <div className="hidden lg:flex items-center w-1/3">
            <div className="relative w-full max-w-xs">
              <input 
                type="text" 
                placeholder="Search collection..." 
                className="w-full bg-gray-50 border border-gray-100 rounded-full py-2 px-4 pl-10 text-xs focus:ring-1 focus:ring-black outline-none"
              />
              <Search className="absolute left-3 top-2.5 text-gray-400" size={14} />
            </div>
          </div>

          {/* Center: Logo */}
          <div className="w-1/3 text-center">
            <Link to="/" className="inline-block">
              <span className="text-2xl font-serif font-black tracking-tighter uppercase">Anvi Studio</span>
            </Link>
          </div>

          {/* Right: Icons */}
          <div className="flex items-center justify-end w-1/3 space-x-5 lg:space-x-7">
            <div className="group relative">
              <button className="flex items-center space-x-1.5 text-gray-700 hover:text-black transition-colors">
                <User size={20} />
                <span className="text-[10px] font-bold uppercase hidden lg:inline tracking-wider">
                  {isAuthenticated ? user.username : 'Account'}
                </span>
              </button>
              <div className="absolute right-0 top-full hidden group-hover:block bg-white shadow-2xl border rounded-sm w-48 py-2 z-50">
                {isAuthenticated ? (
                  <>
                    <Link to="/profile" className="block px-4 py-2 text-xs hover:bg-gray-50">My Profile</Link>
                    <Link to="/orders" className="block px-4 py-2 text-xs hover:bg-gray-50">My Orders</Link>
                    {user.role === 'ADMIN' && <Link to="/admin" className="block px-4 py-2 text-xs font-bold text-blue-600 hover:bg-gray-50">Admin Panel</Link>}
                    <button onClick={logout} className="w-full text-left px-4 py-2 text-xs text-red-600 hover:bg-gray-50 border-t mt-1">Logout</button>
                  </>
                ) : (
                  <>
                    <Link to="/login" className="block px-4 py-2 text-xs hover:bg-gray-50">Login</Link>
                    <Link to="/register" className="block px-4 py-2 text-xs hover:bg-gray-50">Create Account</Link>
                  </>
                )}
              </div>
            </div>
            <Link to="/wishlist" className="text-gray-700 hover:text-black">
              <Heart size={20} />
            </Link>
            <Link to="/cart" className="text-gray-700 hover:text-black relative">
              <ShoppingBag size={20} />
              <span className="absolute -top-1.5 -right-1.5 bg-black text-white text-[9px] w-4 h-4 rounded-full flex items-center justify-center font-bold">0</span>
            </Link>
          </div>
        </div>
      </div>

      {/* Main Navigation links */}
      <nav className={`lg:block border-t ${isMenuOpen ? 'block' : 'hidden'}`}>
        <div className="container mx-auto">
          <ul className="flex flex-col lg:flex-row justify-center items-center lg:space-x-10 text-[10px] font-bold uppercase tracking-[0.2em] py-4 lg:py-3.5 space-y-4 lg:space-y-0">
            <li><Link to="/" className="hover:text-gray-400 transition-colors">Home</Link></li>
            <li><Link to="/products?category=Sarees" className="hover:text-gray-400 transition-colors">Sarees</Link></li>
            <li><Link to="/products?category=Lehengas" className="hover:text-gray-400 transition-colors">Lehengas</Link></li>
            <li><Link to="/products?category=Handlooms" className="hover:text-gray-400 transition-colors text-amber-700">Handlooms</Link></li>
            <li><Link to="/about" className="hover:text-gray-400 transition-colors">About Us</Link></li>
          </ul>
        </div>
      </nav>
    </header>
  );
};

const Footer = () => (
  <footer className="bg-gray-50 border-t pt-20 pb-10 mt-auto">
    <div className="container mx-auto px-4">
      <div className="grid grid-cols-1 md:grid-cols-4 gap-12 text-center md:text-left">
        <div>
          <h2 className="text-lg font-serif font-bold mb-6">ANVI STUDIO</h2>
          <p className="text-gray-500 text-xs leading-relaxed mb-6">Celebrating Indian heritage through handcrafted elegance for the modern woman.</p>
          <div className="flex justify-center md:justify-start space-x-5 text-gray-400">
            <Facebook size={18} /> <Instagram size={18} /> <Twitter size={18} />
          </div>
        </div>
        <div>
          <h4 className="text-[10px] font-bold uppercase tracking-widest mb-6">Shop</h4>
          <ul className="space-y-4 text-xs text-gray-500">
            <li><Link to="/products?category=Sarees">Silk Sarees</Link></li>
            <li><Link to="/products?category=Lehengas">Lehengas</Link></li>
            <li><Link to="/products?category=Kurtis">Designer Kurtis</Link></li>
          </ul>
        </div>
        <div>
          <h4 className="text-[10px] font-bold uppercase tracking-widest mb-6">Help</h4>
          <ul className="space-y-4 text-xs text-gray-500">
            <li><Link to="/shipping">Shipping Policy</Link></li>
            <li><Link to="/returns">Returns & Exchange</Link></li>
            <li><Link to="/contact">Contact Us</Link></li>
          </ul>
        </div>
        <div>
          <h4 className="text-[10px] font-bold uppercase tracking-widest mb-6">Newsletter</h4>
          <p className="text-xs text-gray-500 mb-4">Subscribe for early access to drops.</p>
          <div className="flex flex-col space-y-2">
            <input type="email" placeholder="email@example.com" className="bg-white border border-gray-200 px-4 py-3 text-xs outline-none" />
            <button className="bg-black text-white text-[10px] font-bold uppercase tracking-widest py-3">Join</button>
          </div>
        </div>
      </div>
      <div className="border-t mt-20 pt-8 text-center text-[10px] text-gray-400 tracking-widest uppercase">&copy; {new Date().getFullYear()} Anvi Studio Boutique.</div>
    </div>
  </footer>
);

// --- MAIN APP ---

const Home = () => (
  <div className="container mx-auto px-4 py-16">
    <div className="relative h-[600px] overflow-hidden group">
      <img src="https://images.unsplash.com/photo-1583391733956-6c78276477e2?auto=format&fit=crop&q=80&w=2000" className="w-full h-full object-cover group-hover:scale-105 transition-transform duration-1000" alt="Banner" />
      <div className="absolute inset-0 bg-black/30 flex items-center justify-center text-center">
        <div className="text-white max-w-lg">
          <h1 className="text-6xl font-serif font-bold mb-6">Woven Legacy</h1>
          <p className="text-xs font-bold uppercase tracking-[0.3em] mb-10">Handpicked Festive Collection</p>
          <Link to="/products" className="inline-block bg-white text-black px-12 py-4 text-[10px] font-bold uppercase tracking-widest hover:bg-black hover:text-white transition-colors">Shop Now</Link>
        </div>
      </div>
    </div>
  </div>
);

export default function App() {
  return (
    <AuthProvider>
      <Router>
        <div className="flex flex-col min-h-screen bg-white font-sans">
          <AnnouncementBar />
          <Navbar />
          <main className="flex-grow">
            <Routes>
              <Route path="/" element={<Home />} />
              <Route path="/login" element={<div className="container mx-auto py-24 text-center">Login Page Placeholder</div>} />
              <Route path="/register" element={<div className="container mx-auto py-24 text-center">Register Page Placeholder</div>} />
              <Route path="/products" element={<div className="container mx-auto py-24 text-center text-xs font-bold uppercase tracking-widest">Catalog Loading...</div>} />
            </Routes>
          </main>
          <Footer />
        </div>
      </Router>
    </AuthProvider>
  );
}
