import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Link, useNavigate, useSearchParams } from 'react-router-dom';
import axios from 'axios';
import { 
  ShoppingCart, Heart, User, Search, Menu, X, Mail, Phone, 
  MapPin, LogOut, ArrowRight, Star, Instagram, Facebook, Twitter 
} from 'lucide-react';

// --- API CONFIGURATION ---
const api = axios.create({ baseURL: '/api' });
api.interceptors.request.use((config) => {
  const token = localStorage.getItem('token');
  if (token) config.headers.Authorization = `Bearer ${token}`;
  return config;
});

// --- COMPONENTS ---

const AnnouncementBar = () => (
  <div className="bg-dark text-white text-center py-2 small fw-bold">
    ✨ FREE SHIPPING ON ALL ORDERS ABOVE ₹1,999 | SHOP THE NEW SAREE COLLECTION ✨
  </div>
);

const Navbar = ({ user, setUser, cartCount }) => {
  const navigate = useNavigate();
  const handleLogout = () => {
    localStorage.removeItem('token');
    setUser(null);
    navigate('/login');
  };

  return (
    <header className="bg-white border-bottom sticky-top">
      {/* Main Header Row */}
      <div className="container py-3 d-flex align-items-center justify-content-between">
        <div className="d-none d-lg-flex gap-3 align-items-center col-4">
          <div className="input-group input-group-sm w-75">
            <input type="text" className="form-control border-end-0 bg-light shadow-none" placeholder="Search our collection..." />
            <span className="input-group-text bg-light border-start-0 text-muted"><Search size={16} /></span>
          </div>
        </div>

        <div className="text-center col-4">
          <Link to="/">
            <img src="/images/Chaknik_Logo.png" alt="Anvi Studio" height="65" />
          </Link>
        </div>

        <div className="d-flex align-items-center justify-content-end gap-4 col-4">
          <div className="dropdown d-none d-md-block">
            <button className="btn btn-link text-dark p-0 dropdown-toggle shadow-none text-decoration-none" data-bs-toggle="dropdown">
              <User size={22} />
            </button>
            <ul className="dropdown-menu dropdown-menu-end shadow border-0 p-3">
              {user ? (
                <>
                  <li className="px-2 mb-2 small fw-bold border-bottom pb-2">Hi, {user.username}</li>
                  <li><Link className="dropdown-item rounded" to="/dashboard">My Account</Link></li>
                  <li><Link className="dropdown-item rounded" to="/orders">My Orders</Link></li>
                  <li><hr className="dropdown-divider" /></li>
                  <li><button className="dropdown-item text-danger rounded" onClick={handleLogout}>Logout</button></li>
                </>
              ) : (
                <>
                  <li><Link className="dropdown-item rounded" to="/login">Login</Link></li>
                  <li><Link className="dropdown-item rounded" to="/register">Register</Link></li>
                </>
              )}
            </ul>
          </div>
          <Link to="/wishlist" className="text-dark"><Heart size={22} /></Link>
          <Link to="/cart" className="text-dark position-relative">
            <ShoppingCart size={22} />
            <span className="position-absolute top-0 start-100 translate-middle badge rounded-pill bg-danger" style={{fontSize: '10px'}}>{cartCount}</span>
          </Link>
        </div>
      </div>

      {/* Navigation Menu */}
      <nav className="navbar navbar-expand-lg navbar-light py-0">
        <div className="container justify-content-center">
          <ul className="navbar-nav gap-lg-4 text-uppercase small fw-bold">
            <li className="nav-item"><Link className="nav-link text-dark py-3 border-bottom-hover" to="/">Home</Link></li>
            <li className="nav-item dropdown">
              <a className="nav-link text-dark py-3 dropdown-toggle" href="#" data-bs-toggle="dropdown">Sarees</a>
              <ul className="dropdown-menu border-0 shadow-sm">
                <li><Link className="dropdown-item" to="/products?category=Silk">Silk Sarees</Link></li>
                <li><Link className="dropdown-item" to="/products?category=Cotton">Cotton Sarees</Link></li>
                <li><Link className="dropdown-item" to="/products?category=Handloom">Handloom Collection</Link></li>
              </ul>
            </li>
            <li className="nav-item"><Link className="nav-link text-dark py-3 border-bottom-hover" to="/products?category=Lehengas">Lehengas</Link></li>
            <li className="nav-item"><Link className="nav-link text-dark py-3 border-bottom-hover" to="/products?category=Kurtis">Kurtis</Link></li>
            <li className="nav-item"><Link className="nav-link text-dark py-3 border-bottom-hover" to="/about">Our Story</Link></li>
          </ul>
        </div>
      </nav>
    </header>
  );
};

const Footer = () => (
  <footer className="bg-light pt-5 pb-3 border-top mt-5">
    <div className="container">
      <div className="row g-5">
        <div className="col-lg-4">
          <img src="/images/Chaknik_Logo.png" alt="Logo" height="50" className="mb-4" />
          <p className="text-muted small">Experience the timeless beauty of Indian traditions. Hand-picked fabrics, designer silhouettes, and exceptional craftsmanship delivered worldwide.</p>
          <div className="d-flex gap-3 mt-4">
            <Instagram size={20} className="text-muted" /> <Facebook size={20} className="text-muted" /> <Twitter size={20} className="text-muted" />
          </div>
        </div>
        <div className="col-md-2 col-6">
          <h6 className="fw-bold mb-4">Customer Care</h6>
          <ul className="list-unstyled small text-muted">
            <li className="mb-2">Contact Us</li>
            <li className="mb-2">Track Order</li>
            <li className="mb-2">Shipping Policy</li>
            <li className="mb-2">Returns & Exchange</li>
          </ul>
        </div>
        <div className="col-md-2 col-6">
          <h6 className="fw-bold mb-4">About Us</h6>
          <ul className="list-unstyled small text-muted">
            <li className="mb-2">Our Story</li>
            <li className="mb-2">Artisan Stories</li>
            <li className="mb-2">Careers</li>
            <li className="mb-2">Privacy Policy</li>
          </ul>
        </div>
        <div className="col-lg-4">
          <h6 className="fw-bold mb-4">Subscribe to our Newsletter</h6>
          <p className="small text-muted mb-3">Get exclusive offers and early access to new collections.</p>
          <div className="input-group">
            <input type="email" className="form-control rounded-0 shadow-none border-dark" placeholder="Email Address" />
            <button className="btn btn-dark rounded-0 px-4">JOIN</button>
          </div>
        </div>
      </div>
      <hr className="my-5 opacity-10" />
      <div className="text-center text-muted small">
        © 2024 ANVI STUDIO. ALL RIGHTS RESERVED.
      </div>
    </div>
  </footer>
);

// --- PAGES ---

const HomePage = () => {
  const [featured, setFeatured] = useState([]);
  
  useEffect(() => {
    api.get('/products/featured').then(res => setFeatured(res.data));
  }, []);

  return (
    <div className="animate-in">
      {/* Hero Carousel Replacement */}
      <section className="position-relative overflow-hidden mb-5" style={{height: '650px'}}>
        <img src="/images/licensed-image.png" className="w-100 h-100 object-fit-cover" alt="Hero" />
        <div className="position-absolute top-0 start-0 w-100 h-100 d-flex align-items-center bg-dark bg-opacity-25">
          <div className="container text-white text-center">
            <h5 className="text-uppercase tracking-widest mb-3 animate-up">New Collection 2024</h5>
            <h1 className="display-1 fw-bold mb-4 animate-up" style={{fontFamily: 'serif'}}>The Silk Route</h1>
            <Link to="/products" className="btn btn-light rounded-0 px-5 py-3 fw-bold animate-up">SHOP THE COLLECTION</Link>
          </div>
        </div>
      </section>

      <div className="container">
        {/* Category Circles */}
        <section className="py-5 mb-5">
          <h3 className="text-center fw-bold mb-5 text-uppercase tracking-widest">Shop By Category</h3>
          <div className="row g-4 justify-content-center">
            {[
              {name: 'Sarees', img: '/images/shopcategory/sarees.png'},
              {name: 'Lehengas', img: '/images/shopcategory/lehengas.png'},
              {name: 'Kurtis', img: '/images/shopcategory/kurthi&suits.png'},
              {name: 'Jewelry', img: '/images/shopcategory/kidswear.png'}
            ].map((cat, i) => (
              <div key={i} className="col-md-2 col-6 text-center">
                <Link to={`/products?category=${cat.name}`} className="text-decoration-none group">
                  <div className="rounded-circle overflow-hidden mx-auto mb-3 border p-1 transition-all" style={{width: '150px', height: '150px'}}>
                    <img src={cat.img} className="w-100 h-100 object-fit-cover rounded-circle transition-transform" alt={cat.name} />
                  </div>
                  <h6 className="text-dark fw-bold">{cat.name}</h6>
                </Link>
              </div>
            ))}
          </div>
        </section>

        {/* Featured Products */}
        <section className="py-5">
          <div className="d-flex justify-content-between align-items-end mb-5">
            <h2 className="fw-bold m-0 text-uppercase">Bestsellers</h2>
            <Link to="/products" className="text-dark small fw-bold">VIEW ALL &rarr;</Link>
          </div>
          <div className="row g-4">
            {featured.length > 0 ? featured.map(p => (
              <div key={p.id} className="col-lg-3 col-6">
                <div className="card h-100 border-0 product-card">
                  <div className="position-relative overflow-hidden" style={{height: '380px'}}>
                    <img src={p.imageUrl || '/images/sarees/saree-image.jpg'} className="w-100 h-100 object-fit-cover transition-transform" alt={p.name} />
                    <button className="btn btn-white position-absolute bottom-0 start-0 w-100 rounded-0 border-top py-2 opacity-0 transition-all fw-bold">ADD TO CART</button>
                    {p.discountPercent > 0 && <span className="position-absolute top-0 start-0 bg-danger text-white px-2 py-1 small m-2">-{p.discountPercent}%</span>}
                  </div>
                  <div className="card-body px-0 py-3">
                    <p className="text-muted small mb-1 text-uppercase">{p.categoryName}</p>
                    <h6 className="fw-bold text-truncate">{p.name}</h6>
                    <div className="d-flex align-items-center gap-2">
                      <span className="fw-bold">₹{p.discountedPrice}</span>
                      {p.price > p.discountedPrice && <span className="text-muted text-decoration-line-through small">₹{p.price}</span>}
                    </div>
                  </div>
                </div>
              </div>
            )) : (
              <div className="col-12 text-center py-5">Loading our stunning collection...</div>
            )}
          </div>
        </section>
      </div>
    </div>
  );
};

// --- AUTH PAGES (LOGIN/REGISTER) ---
// (Simplified versions for brevity, matching the look)
const AuthPage = ({ type, onLoginSuccess }) => {
  return (
    <div className="container py-5 mt-5">
      <div className="row justify-content-center">
        <div className="col-md-5">
          <div className="card border-0 shadow-lg p-5">
            <h2 className="text-center fw-bold mb-4">{type === 'login' ? 'Login' : 'Register'}</h2>
            <form>
              <div className="mb-3"><input type="text" className="form-control rounded-0 p-3" placeholder="Email Address" required /></div>
              <div className="mb-4"><input type="password" className="form-control rounded-0 p-3" placeholder="Password" required /></div>
              <button className="btn btn-dark w-100 py-3 rounded-0 fw-bold">{type === 'login' ? 'SIGN IN' : 'CREATE ACCOUNT'}</button>
            </form>
          </div>
        </div>
      </div>
    </div>
  );
};

// --- MAIN APP ---

export default function App() {
  const [user, setUser] = useState(null);

  useEffect(() => {
    api.get('/auth/me').then(res => setUser(res.data)).catch(() => {});
  }, []);

  return (
    <Router>
      <div className="d-flex flex-column min-vh-100">
        <AnnouncementBar />
        <Navbar user={user} setUser={setUser} cartCount={0} />
        
        <main className="flex-grow-1">
          <Routes>
            <Route path="/" element={<HomePage />} />
            <Route path="/login" element={<AuthPage type="login" onLoginSuccess={setUser} />} />
            <Route path="/register" element={<AuthPage type="register" />} />
          </Routes>
        </main>

        <Footer />
      </div>

      <style>{`
        body { font-family: 'Playfair Display', serif; }
        .tracking-widest { letter-spacing: 0.2rem; }
        .border-bottom-hover { transition: border-color 0.3s; border-bottom: 2px solid transparent; }
        .border-bottom-hover:hover { border-bottom-color: black; }
        .transition-transform { transition: transform 0.5s ease; }
        .group:hover .transition-transform { transform: scale(1.1); }
        .product-card:hover .btn { opacity: 1 !important; }
        .product-card:hover .transition-transform { transform: scale(1.05); }
        .animate-up { animation: fadeInUp 0.8s forwards; opacity: 0; }
        @keyframes fadeInUp {
          from { opacity: 0; transform: translateY(30px); }
          to { opacity: 1; transform: translateY(0); }
        }
      `}</style>
    </Router>
  );
}
