import React, { createContext, useContext, useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Link, useNavigate, useSearchParams } from 'react-router-dom';
import axios from 'axios';
import { 
  ShoppingCart, 
  Heart, 
  User, 
  Search, 
  Menu, 
  X, 
  Mail, 
  Phone, 
  MapPin, 
  LogOut, 
  ArrowRight,
  ShieldCheck,
  Star,
  ShoppingBag,
  Facebook,
  Instagram,
  Twitter,
  ChevronDown,
  ChevronRight
} from 'lucide-react';

// --- 1. API CLIENT CONFIGURATION ---
// We integrate the API client directly to handle base URLs and JWT tokens.
const apiClient = axios.create({
  baseURL: 'http://localhost:8080/api',
  headers: {
    'Content-Type': 'application/json',
  },
});

apiClient.interceptors.request.use(
  (config) => {
    const token = localStorage.getItem('token');
    if (token) {
      config.headers.Authorization = `Bearer ${token}`;
    }
    return config;
  },
  (error) => Promise.reject(error)
);

apiClient.interceptors.response.use(
  (response) => response,
  (error) => {
    if (error.response?.status === 401) {
      localStorage.removeItem('token');
      if (window.location.pathname !== '/login') {
        window.location.href = '/login';
      }
    }
    return Promise.reject(error);
  }
);

// --- 2. AUTH CONTEXT ---
const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const checkAuth = async () => {
      const token = localStorage.getItem('token');
      if (token) {
        try {
          const response = await apiClient.get('/auth/me');
          setUser(response.data);
        } catch (error) {
          console.error("Session expired or invalid token");
          localStorage.removeItem('token');
        }
      }
      setLoading(false);
    };

    checkAuth();
  }, []);

  const login = async (username, password) => {
    try {
      const response = await apiClient.post('/auth/login', { username, password });
      const { token, user: userData } = response.data;
      
      localStorage.setItem('token', token);
      setUser(userData);
      return { success: true };
    } catch (error) {
      return { 
        success: false, 
        message: error.response?.data?.error || "Login failed. Please try again." 
      };
    }
  };

  const logout = () => {
    localStorage.removeItem('token');
    setUser(null);
  };

  const register = async (registrationData) => {
    try {
      const response = await apiClient.post('/auth/register', registrationData);
      return { success: true, message: response.data.message };
    } catch (error) {
      return { 
        success: false, 
        message: error.response?.data?.error || "Registration failed." 
      };
    }
  };

  const value = {
    user,
    loading,
    login,
    logout,
    register,
    isAuthenticated: !!user,
    isAdmin: user?.role === 'ADMIN'
  };

  return (
    <AuthContext.Provider value={value}>
      {!loading && children}
    </AuthContext.Provider>
  );
};

export const useAuth = () => {
  const context = useContext(AuthContext);
  if (!context) {
    throw new Error('useAuth must be used within an AuthProvider');
  }
  return context;
};

// --- 3. LAYOUT COMPONENTS ---

const AnnouncementBar = () => (
  <div className="bg-black text-white text-center py-2.5 text-[10px] font-bold tracking-[0.2em] uppercase">
    ✨ Free Shipping on orders over ₹2,000 | Discover the New Festive Silk Collection ✨
  </div>
);

const Navbar = () => {
  const { user, logout, isAuthenticated } = useAuth();
  
  return (
    <header className="bg-white border-b sticky top-0 z-50">
      <div className="container mx-auto px-4 py-4 flex items-center justify-between">
        {/* Left: Search (Desktop) */}
        <div className="w-1/3">
          <div className="relative max-w-xs hidden md:block">
            <input 
              type="text" 
              placeholder="Search boutique..." 
              className="w-full bg-gray-50 border border-gray-200 rounded-full py-2 px-4 pl-10 text-xs focus:ring-1 focus:ring-black outline-none"
            />
            <Search className="absolute left-3 top-2.5 text-gray-400" size={14} />
          </div>
        </div>

        {/* Center: Logo */}
        <div className="w-1/3 text-center">
          <Link to="/" className="text-2xl font-serif font-black tracking-tighter hover:opacity-75 transition-opacity">
            ANVI STUDIO
          </Link>
        </div>

        {/* Right: Icons */}
        <div className="w-1/3 flex items-center justify-end space-x-6">
          <div className="group relative">
            <button className="flex items-center space-x-1.5 text-gray-700 hover:text-black transition-colors">
              <User size={20} />
              <span className="text-[10px] font-bold uppercase hidden lg:inline tracking-wider">
                {isAuthenticated ? user.username : 'Account'}
              </span>
            </button>
            <div className="absolute right-0 top-full hidden group-hover:block bg-white shadow-2xl border rounded-sm w-48 py-2 z-50">
              {isAuthenticated ? (
                <>
                  <Link to="/profile" className="block px-4 py-2 text-xs hover:bg-gray-50">My Profile</Link>
                  <Link to="/orders" className="block px-4 py-2 text-xs hover:bg-gray-50">My Orders</Link>
                  <button onClick={logout} className="w-full text-left px-4 py-2 text-xs text-red-600 hover:bg-gray-50">Logout</button>
                </>
              ) : (
                <>
                  <Link to="/login" className="block px-4 py-2 text-xs hover:bg-gray-50">Login</Link>
                  <Link to="/register" className="block px-4 py-2 text-xs hover:bg-gray-50">Register</Link>
                </>
              )}
            </div>
          </div>
          <Link to="/wishlist" className="text-gray-700 hover:text-black transition-colors">
            <Heart size={20} />
          </Link>
          <Link to="/cart" className="text-gray-700 hover:text-black relative transition-colors">
            <ShoppingBag size={20} />
            <span className="absolute -top-1.5 -right-1.5 bg-black text-white text-[9px] w-4.5 h-4.5 rounded-full flex items-center justify-center font-bold">0</span>
          </Link>
        </div>
      </div>
      
      <nav className="border-t py-3.5 bg-white">
        <ul className="flex justify-center space-x-10 text-[10px] font-bold uppercase tracking-[0.15em]">
          <li><Link to="/" className="hover:text-gray-400 transition-colors">Home</Link></li>
          <li><Link to="/collection/sarees" className="hover:text-gray-400 transition-colors">Sarees</Link></li>
          <li><Link to="/collection/lehengas" className="hover:text-gray-400 transition-colors">Lehengas</Link></li>
          <li><Link to="/collection/handloom" className="hover:text-gray-400 transition-colors">Handloom</Link></li>
          <li><Link to="/collection/new" className="hover:text-gray-400 transition-colors">New Arrivals</Link></li>
        </ul>
      </nav>
    </header>
  );
};

const Footer = () => (
  <footer className="bg-gray-50 border-t pt-20 pb-10">
    <div className="container mx-auto px-4">
      <div className="grid grid-cols-1 md:grid-cols-4 gap-12 mb-16 text-center md:text-left">
        <div className="md:col-span-1">
          <h2 className="text-lg font-serif font-bold mb-6 tracking-tight">ANVI STUDIO</h2>
          <p className="text-gray-500 text-xs leading-relaxed mb-6 max-w-xs mx-auto md:mx-0">
            Handpicked elegance for the modern woman, celebrating centuries of Indian textile heritage through artisanal craftsmanship.
          </p>
          <div className="flex justify-center md:justify-start space-x-5">
            <Facebook size={18} className="text-gray-400 hover:text-black cursor-pointer transition-colors" />
            <Instagram size={18} className="text-gray-400 hover:text-black cursor-pointer transition-colors" />
            <Twitter size={18} className="text-gray-400 hover:text-black cursor-pointer transition-colors" />
          </div>
        </div>
        
        <div>
          <h4 className="text-[10px] font-bold uppercase tracking-widest mb-6">Collections</h4>
          <ul className="space-y-4 text-xs text-gray-500">
            <li><Link to="/collection/silk" className="hover:text-black transition-colors">Silk Sarees</Link></li>
            <li><Link to="/collection/cotton" className="hover:text-black transition-colors">Cotton Sarees</Link></li>
            <li><Link to="/collection/handloom" className="hover:text-black transition-colors">Handloom</Link></li>
            <li><Link to="/collection/new" className="hover:text-black transition-colors">New Arrivals</Link></li>
          </ul>
        </div>

        <div>
          <h4 className="text-[10px] font-bold uppercase tracking-widest mb-6">Support</h4>
          <ul className="space-y-4 text-xs text-gray-500">
            <li><Link to="/shipping" className="hover:text-black transition-colors">Shipping Policy</Link></li>
            <li><Link to="/returns" className="hover:text-black transition-colors">Returns & Exchange</Link></li>
            <li><Link to="/contact" className="hover:text-black transition-colors">Contact Us</Link></li>
            <li><Link to="/faq" className="hover:text-black transition-colors">FAQs</Link></li>
          </ul>
        </div>

        <div>
          <h4 className="text-[10px] font-bold uppercase tracking-widest mb-6">Newsletter</h4>
          <p className="text-xs text-gray-500 mb-4">Join for exclusive early access to drops and style inspiration.</p>
          <div className="flex flex-col space-y-2 max-w-xs mx-auto md:mx-0">
            <input 
              type="email" 
              placeholder="email@example.com" 
              className="bg-white border border-gray-200 px-4 py-3 text-xs outline-none focus:border-black transition-colors"
            />
            <button className="bg-black text-white text-[10px] font-bold uppercase tracking-widest py-3 hover:bg-gray-800 transition-colors">
              Subscribe
            </button>
          </div>
        </div>
      </div>
      <div className="border-t pt-8 text-center text-[10px] text-gray-400 tracking-widest uppercase font-bold">
        &copy; {new Date().getFullYear()} Anvi Studio Boutique. All Rights Reserved.
      </div>
    </div>
  </footer>
);

// --- 4. MAIN APP ---

const HomePage = () => {
  const [featuredProducts, setFeaturedProducts] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchFeatured = async () => {
      try {
        const response = await apiClient.get('/products/featured');
        setFeaturedProducts(response.data);
      } catch (error) {
        console.error("Failed to load featured products");
      } finally {
        setLoading(false);
      }
    };
    fetchFeatured();
  }, []);

  return (
    <div className="animate-in fade-in duration-700">
      {/* Hero Section */}
      <section className="relative h-[650px] bg-gray-100 overflow-hidden">
        <img 
          src="https://images.unsplash.com/photo-1583391733956-6c78276477e2?auto=format&fit=crop&q=80&w=2000" 
          className="w-full h-full object-cover transition-transform duration-[2000ms] hover:scale-105" 
          alt="Hero Banner" 
        />
        <div className="absolute inset-0 bg-black/30 flex items-center">
          <div className="container mx-auto px-4">
            <div className="max-w-xl text-white">
              <span className="block text-[10px] font-bold uppercase tracking-[0.4em] mb-4">New Arrivals 2024</span>
              <h2 className="text-6xl font-serif font-bold mb-8 leading-[1.1]">Timeless Elegance, <br/>Woven with Love.</h2>
              <p className="text-sm mb-10 opacity-90 tracking-wide font-light max-w-sm leading-relaxed">Discover our handpicked collection of artisanal sarees and designer lehengas curated just for you.</p>
              <Link to="/collection/all" className="inline-block bg-white text-black px-12 py-4 text-[10px] font-bold uppercase tracking-widest hover:bg-black hover:text-white transition-all transform hover:-translate-y-1">
                Shop Collection
              </Link>
            </div>
          </div>
        </div>
      </section>

      {/* Featured Categories */}
      <section className="py-24 container mx-auto px-4">
        <div className="flex flex-col items-center text-center mb-16">
          <h3 className="text-2xl font-serif font-bold mb-3 uppercase tracking-tighter">Shop by Category</h3>
          <div className="w-12 h-0.5 bg-black"></div>
        </div>
        <div className="grid grid-cols-1 md:grid-cols-3 gap-8">
          {[
            { name: 'Pure Silk Sarees', img: 'https://images.unsplash.com/photo-1610030469983-98e550d6193c?auto=format&fit=crop&q=80&w=800' },
            { name: 'Designer Lehengas', img: 'https://images.unsplash.com/photo-1594633312681-425c7b97ccd1?auto=format&fit=crop&q=80&w=800' },
            { name: 'Handloom Cotton', img: 'https://images.unsplash.com/photo-1617627143750-d86bc21e42bb?auto=format&fit=crop&q=80&w=800' }
          ].map((cat, i) => (
            <Link key={i} to={`/collection/${cat.name.toLowerCase()}`} className="group relative block aspect-[4/5] overflow-hidden bg-gray-200 shadow-sm">
              <img src={cat.img} alt={cat.name} className="absolute inset-0 w-full h-full object-cover transition-transform duration-700 group-hover:scale-110" />
              <div className="absolute inset-0 bg-black/20 group-hover:bg-black/40 transition-colors flex flex-col items-center justify-center p-4">
                <h4 className="text-white text-xl font-serif font-bold tracking-wider mb-2">{cat.name}</h4>
                <span className="text-white text-[9px] font-bold uppercase tracking-widest opacity-0 group-hover:opacity-100 transition-opacity flex items-center gap-2">
                  Explore <ChevronRight size={12} />
                </span>
              </div>
            </Link>
          ))}
        </div>
      </section>
    </div>
  );
};

const App = () => {
  return (
    <AuthProvider>
      <Router>
        <div className="flex flex-col min-h-screen font-sans selection:bg-black selection:text-white bg-white">
          <AnnouncementBar />
          <Navbar />
          
          <main className="flex-grow">
            <Routes>
              <Route path="/" element={<HomePage />} />
              <Route path="/login" element={<div className="flex items-center justify-center h-[60vh] text-[10px] font-bold uppercase tracking-widest text-gray-400">Login Form Placeholder</div>} />
              <Route path="/register" element={<div className="flex items-center justify-center h-[60vh] text-[10px] font-bold uppercase tracking-widest text-gray-400">Registration Form Placeholder</div>} />
              <Route path="/collection/:id" element={<div className="container mx-auto py-24 text-center">Collection Details</div>} />
            </Routes>
          </main>

          <Footer />
        </div>
      </Router>
    </AuthProvider>
  );
};

export default App;
